app.controller("indexCtrl",["$scope","$injector","$location",function(n,t){t.invoke(["indexLouder","$location",function(t,e){n.userid=e.search(),""!=n.userid&&n.userid?(t.getListinfo("task").then(function(t){n.myTask=t.data}),n.indexInfo={panel_1:!0,panel_2:!1,panel_3:!1,switch:function(e){n.indexInfo.panel_1=!1,n.indexInfo.panel_2=!1,n.indexInfo.panel_3=!1,n.indexInfo["panel_"+e]=!0,1==e?t.getListinfo("task").then(function(t){n.myTask=t.data}):2==e?t.getListinfo("todo").then(function(t){n.myToDo=t.data}):3==e&&t.getListinfo("done").then(function(t){n.myDone=t.data})}},n.toDetail=function(t){window.location.href="#!/taskDetail?userid="+n.userid.userid+"&taskid="+t.TaskID},document.title="任务中心"):alert("参数错误")}])}]);